import Layout from '../components/Layout';
import PollPreview from '../components/PollPreview';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Alert from 'react-bootstrap/Alert';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPoll, faQuestionCircle, faEye } from '@fortawesome/free-solid-svg-icons';
import Router from 'next/router';
import absoluteUrl from 'next-absolute-url';
import { withRouter } from 'next/router';
import CategoriesList from '../helpers/CategoriesList';

//TO DO ##############################################
//Validate Poll Inputs

const visibilityTooltip = props => (
  <div
    {...props}
    style={{
      maxWidth: '400px',
      backgroundColor: '#1c2c40',
      padding: '0.5rem 0.75rem',
      color: '#e6e6e6',
      borderRadius: '0.25rem',
      ...props.style,
    }}
  >
    Set whether to allow the public to see this poll or keep it private.
    Only those with the URL will be able to access the poll if set private.
  </div>
);

const votingPeriodTooltip = props => (
  <div
    {...props}
    style={{
      maxWidth: '400px',
      backgroundColor: '#1c2c40',
      padding: '0.5rem 0.75rem',
      color: '#e6e6e6',
      borderRadius: '0.25rem',
      ...props.style,
    }}
  >
    Set in hours when the poll will expire.
  </div>
);

function validatePollInput(pollDataObj) {
  let choices = [pollDataObj.choice1, pollDataObj.choice2];
  if(pollDataObj.choice3 !== '') {
    choices.push(pollDataObj.choice3);
  }
  if(pollDataObj.choice4 !== '') {
    choices.push(pollDataObj.choice4);
  }

  const pollData = {
    title: pollDataObj.title,
    desc: pollDataObj.desc,
    choices: choices,
    visibility: pollDataObj.visibility,
    votingPeriod: pollDataObj.votingPeriod,
    category: pollDataObj.category
  }

  return pollData;
}

class CreatePoll extends React.Component {
  constructor() {
    super();

    this.state = {
      title: '',
      desc: '',
      choice1: '',
      choice2: '',
      choice3: '',
      choice4: '',
      visibility: 'public',
      votingPeriod: 6,
      dateCreated: new Date(),
      category: 0,
      error: false,
      timelimit: 'Voting ends in: 6 hours'
    }

    this.inputUpdate = this.inputUpdate.bind(this);
    this.visibilityUpdate = this.visibilityUpdate.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.updateTimePeriod = this.updateTimePeriod.bind(this);
    this.updateCategory = this.updateCategory.bind(this);
    this.setHourPreset = this.setHourPreset.bind(this);
  }

  inputUpdate(e) {
    this.setState({ [e.target.name]: e.target.value })
  }

  updateCategory(e) {
    this.setState({ category: e.target.selectedIndex });
  }

  visibilityUpdate(e) {
    this.setState({ visibility: e.target.value });
  }

  async handleSubmit(e, req) {
    e.preventDefault();
    const { origin } = absoluteUrl(req);
    const pollData = validatePollInput(this.state);
    try {
      const res = await fetch(`${origin}/api/create-poll`, {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          'X-Origin': 'statmix'
        },
        body: JSON.stringify(pollData)
      });
      const data = await res.json();
      if(data.message === 'success') {
        Router.push(`/poll/${data.url}`);
      } else {
        this.setState({ error: true });
        window.scrollTo({
          top: 56,
          left: 0,
          behavior: 'smooth'
        });
      }
      //console.log(data);
    } catch(err) {
      //console.log(err)
    }
  }

  updateTimePeriod(e) {
   this.setState({ [e.target.name]: e.target.value });
  }

  setHourPreset(e) {
    this.setState({ votingPeriod: e.target.dataset['hours'] });
  }

  render() {
    const {
      title,
      desc,
      choice1,
      choice2,
      choice3,
      choice4,
      votingPeriod,
      error
    } = this.state;

    return (
      <Layout
        pageTitle='Create Poll'
        path={this.props.router.asPath}
      >
        <h4 className='page-header'><FontAwesomeIcon icon={faPoll} /> Create Poll</h4>
        <hr />
        { error ?
          <Alert variant='danger'>
           <b>Error submitting poll</b>
          </Alert> : null
        }
        <Form autoComplete='off' onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Label>
              Title
            </Form.Label>
            <Form.Control
              value={title}
              onChange={this.inputUpdate}
              type='text' name='title' maxLength='120'
              required
            />
          </Form.Group>
          <Form.Group>
            <Form.Label>
              Description (Optional)
            </Form.Label>
            <Form.Control
              value={desc}
              onChange={this.inputUpdate}
              style={{ 'maxHeight': '144px', minHeight: '72px' }}
              as="textarea" rows="3" name='desc' maxLength='500'
            />
            <Form.Text>
              Characters remaining: {500 - desc.length}
            </Form.Text>
          </Form.Group>
          <Form.Row>
            <Form.Group as={Col}>
              <Form.Label>
                Choice #1
              </Form.Label>
              <Form.Control
                value={choice1}
                onChange={this.inputUpdate}
                type='text' name='choice1' maxLength='75'
                required
              />
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>
                Choice #2
              </Form.Label>
              <Form.Control
                value={choice2}
                onChange={this.inputUpdate} 
                type='text' name='choice2' maxLength='75'
                required
              />
            </Form.Group>
          </Form.Row>
          <Form.Row>
            <Form.Group as={Col}>
              <Form.Label>
                Choice #3 (Optional)
              </Form.Label>
              <Form.Control
                value={choice3}
                onChange={this.inputUpdate}
                type='text' name='choice3' maxLength='75'
              />
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>
                Choice #4 (Optional)
              </Form.Label>
              <Form.Control
                value={choice4}
                onChange={this.inputUpdate}
                type='text'name='choice4' maxLength='75'
              />
            </Form.Group>
          </Form.Row>
          <hr />
          <Form.Row>
            <Form.Group as={Col}>
              <Form.Label>
                Visibility{" "}
                <OverlayTrigger
                    placement="top-start"
                    delay={{ show: 250, hide: 400 }}
                    overlay={visibilityTooltip}>
                  <FontAwesomeIcon icon={faQuestionCircle} />
                </OverlayTrigger>
              </Form.Label>
              <Form.Check
                onClick={this.visibilityUpdate}
                type='radio' label='Public' name='visibility' value='public' defaultChecked
              />
              <Form.Check
                onClick={this.visibilityUpdate}
                type='radio' label='Private' name='visibility' value='private'
              />
            </Form.Group>
            <Form.Group as={Col}>
              <Form.Label>
                Voting Period (hours){" "}
                <OverlayTrigger
                  placement="top-start"
                  delay={{ show: 250, hide: 400 }}
                  overlay={votingPeriodTooltip}
                >
                  <FontAwesomeIcon icon={faQuestionCircle} />
                </OverlayTrigger>
              </Form.Label>
              <Form.Control
                value={votingPeriod}
                onChange={this.updateTimePeriod}
                style={{ maxWidth: '200px' }}
                type='number' min='6' max='168h' name='votingPeriod'
                className="mb-1"
              />
              <ButtonGroup size="sm">
                <Button variant="grey-blue" onClick={this.setHourPreset} data-hours='24'>1 day</Button>
                <Button variant="grey-blue" onClick={this.setHourPreset} data-hours='72'>3 days</Button>
                <Button variant="grey-blue" onClick={this.setHourPreset} data-hours='168'>7 days</Button>
              </ButtonGroup>
              <Form.Text>
                6h - 168h (1 week) 
              </Form.Text>
            </Form.Group>
          </Form.Row>
          <Form.Group>
            <Form.Label>
              Category
            </Form.Label>
            <Form.Control
              onChange={this.updateCategory}
              as="select"
              name='category'
            >
            <option>Select a category</option>
            {CategoriesList.map((category, i) => (
              <option key={i}>{category}</option>
            ))}
            </Form.Control>
          </Form.Group>
          <div className='poll-preview'>
            <h4 className='page-header'><FontAwesomeIcon icon={faEye} /> Preview</h4>
            <hr />
            <PollPreview {...this.state} />
          </div>
          <hr />
          <Form.Group style={{ display: 'flex', justifyContent: 'center', marginTop: '2rem' }}>
            <Button 
              variant='light-blue' type="submit" style={{ width: '200px' }}>
              Create Poll
            </Button>
          </Form.Group>
        </Form>
      </Layout>
    )
  }
}

export default withRouter(CreatePoll);
